import React, { Component } from "react";
import {
  Route,
  BrowserRouter,
  Link,
  NavLink,
  Redirect,
  Prompt
} from "react-router-dom";
import Footer from "./components/footer";

import logo from "./logo.svg";
import "./App.css";
import Home from "./screens/home";
import Navbar from "./components/navbar";
import MainSlider from "./components/mainSlider";
import SingleProduct from "./components/singleProduct";
import ShopGrid from "./components/shopGrid";
import Signin from "./components/signin";
import Shopingcart from "./components/shopingcart";
import Shipping from "./components/shipping";
import Orders from "./components/orders";
import Account from "./components/account";
import Seemore from "./components/seemore";
import { push } from 'react-router-redux';
import { Style } from "react-style-tag";
import {Provider} from 'react-redux'
import {
  BrowserView,
  MobileView,
  isBrowser,
  isMobile
} from "react-device-detect";
import loadjs from "loadjs";
import store from './store';
const mapStateToProps = state => {
  return {
    appLoaded: state.common.appLoaded,
    appName: state.common.appName,
    currentUser: state.common.currentUser,
    redirectTo: state.common.redirectTo
  }};

const mapDispatchToProps = dispatch => ({
  onLoad: (payload, token) =>
    dispatch({ type: APP_LOAD, payload, token, skipTracking: true }),
  onRedirect: () =>
    dispatch({ type: REDIRECT })
});

class App extends React.Component {
  componentWillReceiveProps(nextProps) {
    if (nextProps.redirectTo) {
      // this.context.router.replace(nextProps.redirectTo);
      store.dispatch(push(nextProps.redirectTo));
      this.props.onRedirect();
    }
  }

  componentWillMount() {
    const token = window.localStorage.getItem('jwt');
    if (token) {
      agent.setToken(token);
    }

    this.props.onLoad(token ? agent.Auth.current() : null, token);
  }
  componentWillMount() {
    loadjs("js/vendor/modernizr-3.5.0.min.js", function() {
      loadjs("js/vendor/jquery-3.2.1.min.js", function() {
        loadjs("js/popper.min.js", function() {
          loadjs("js/plugins.js", function() {
            loadjs("js/bootstrap.min.js", function() {
              loadjs("js/active.js", function() {
                loadjs("js/main.js");
              });
            });
          });
        });
      });
    });
  }

  render() {
    if (this.props.appLoaded) {
    return (
      <Provider store={store}>
        <BrowserRouter>
        <div class="wrapper" id="wrapper">

          <Navbar   />

          <Route
            exact
            path="/"
            exact
            strict
            render={props => <Home {...props} />}
          />

          <Route
            exact
            path="/product"
            exact
            strict
            render={props => <SingleProduct {...props}/>}
          />

          <Route
            // exact
            strict
            path="/shopGrid"
            // exact

            handler={ShopGrid}
            render={props => <ShopGrid {...props}/>}
          />

          <Route
            exact
            path="/signin"
            exact
            strict
            render={props => <Signin {...props}/>}
          />

          <Route
            exact
            path="/cart"
            exact
            strict
            render={props => <Shopingcart {...props}/>}
          />

          <Route
            exact
            path="/shipping"
            exact
            strict
            render={props => <Shipping {...props}/>}
          />

          <Route
            exact
            path="/myorders"
            exact
            strict
            render={props => <Orders {...props}/>}
          />

          <Route
            exact
            path="/myaccount"
            exact
            strict
            render={props => <Account {...props}/>}
          />

          <Route
            exact
            path="/seemore"
            exact
            strict
            render={props => <Seemore {...props}/>}
          />

          <Footer />
        </div>
      </BrowserRouter>
      </Provider>

    );
  }
}
}

export default App;
